import React, { useEffect } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import styled, { ThemeProvider } from "styled-components";
import { useRecoilState } from "recoil";
import { toDoState, boardState, isLightState, IToDoState } from "./atom";
import Board, { IBoardProps } from "./components/Board";
import { onDragEnd } from "./hooks/useOnDragEnd";
import { MaterialIcon } from "./components/Board";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
  gap: 30px;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Boards = styled.div`
  display: grid;
  width: 100%;
  gap: 10px;
  grid-template-columns: repeat(3, 1fr);
`;

const Navigation = styled.nav`
  display: flex;
  position: fixed;
  padding: 2.5rem 3rem;
  align-items: center;
  justify-content: space-between;
  width: 100vw;
  color: ${(props) => props.theme.textColor};
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 600;
  transition: color 0.3s;
`;

const Buttons = styled.div`
  display: flex;
  align-items: center;
  gap: 2rem;
  transition: color 0.3s;
  color: ${(props) => props.theme.secondaryTextColor};
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.8rem;
  width: 2rem;
  height: 2rem;
  background: none;
  border: none;
  transition: color 0.3s;
  padding: 0;
  border-radius: 0.2rem;
  &:hover,
  &:focus {
    cursor: pointer;
    color: ${(props) => props.theme.accentColor};
  }
  &:focus {
    outline: 0.15rem solid ${(props) => props.theme.accentColor};
  }
`;

const App = () => {
  const [toDos, setToDos] = useRecoilState(toDoState);
  const [boards, setBoards] = useRecoilState(boardState);
  const [isLight, setIsLight] = useRecoilState(isLightState);

  const onAdd = () => {
    const name = window.prompt("새 보드의 이름을 입력해주세요.")?.trim();

    if (name !== null && name !== undefined) {
      if (name === "") {
        alert("이름을 입력해주세요.");
        return;
      }
      setToDos((prev): prev is IToDoState => {
        return [...prev, { title: name, id: Date.now(), toDos: [] }];
      });
    }
  };

  const toggleTheme = () => setIsLight((current) => !current);

  useEffect(() => {
    // 사용자의 기본 theme 추적
    window
      .matchMedia("(prefers-color-scheme: light")
      .addEventListener("change", (e) => {
        setIsLight(e.matches);
      });
  });

  return (
    <>
      <Navigation>
        <Title>당신의 할 일</Title>
        <Buttons>
          <Button onClick={onAdd}>
            <MaterialIcon name="library_add" />
          </Button>
          <Button onClick={toggleTheme}>
            <MaterialIcon name={isLight ? "dark_mode" : "light_mode"} />
          </Button>
        </Buttons>
      </Navigation>
      <DragDropContext
        onDragEnd={(info) => onDragEnd(info, setBoards, setToDos)}
      >
        <Wrapper>
          <Boards>
            {Object.keys(toDos).map((boardId) => (
              <Board boardId={boardId} key={boardId} toDos={toDos[boardId]} />
            ))}
          </Boards>
        </Wrapper>
      </DragDropContext>
    </>
  );
};

export default App;
