import React from "react";
import { DragDropContext } from "react-beautiful-dnd";
import styled, { ThemeProvider } from "styled-components";
import { useRecoilState } from "recoil";
import { toDoState, boardState } from "./atom";
import Board, { IBoardProps } from "./components/Board";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
  gap: 30px;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Boards = styled.div`
  display: grid;
  width: 100%;
  gap: 10px;
  grid-template-columns: repeat(3, 1fr);
`;

const Navigation = styled.nav`
  display: flex;
  position: fixed;
  padding: 2.5rem 3rem;
  align-items: center;
  justify-content: space-between;
  width: 100vw;
  color: ${(props) => props.theme.textColor};
`;

const App = () => {
  const [toDos, setToDos] = useRecoilState(toDoState);
  const [boards, setBoards] = useRecoilState(boardState);
  // const [isLight, setIsLight] = useRecoilState(isLightState);
  // const toggleTheme = () => setIsLight((current) => !current);

  return (
    <>
      <Navigation>
        <Title>당신의 할 일</Title>
        <Buttons>
          <Button onClick={onAdd}>
            <MaterialIcon name="library_add" />
          </Button>
          <Button onClick={toggleTheme}>
            <MaterialIcon name={isLight ? "dark_mode" : "light_mode"} />
          </Button>
        </Buttons>
      </Navigation>
      <DragDropContext onDragEnd={(info) => onDragEnd(info, setTodos)}>
        <Wrapper>
          <Boards>
            {Object.keys(toDos).map((boardId) => (
              <Board boardId={boardId} key={boardId} toDos={toDos[boardId]} />
            ))}
          </Boards>
        </Wrapper>
      </DragDropContext>
    </>
  );
};

export default App;
