import React from "react";
import { DragDropContext, DropResult } from "react-beautiful-dnd";
import styled, { ThemeProvider } from "styled-components";
import { useRecoilState } from "recoil";
import { toDoState } from "./atom";
import Board, { IBoardProps } from "./components/Board";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
  gap: 30px;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Boards = styled.div`
  display: grid;
  width: 100%;
  gap: 10px;
  grid-template-columns: repeat(3, 1fr);
`;

const DeleteSection = styled.div`
  display: flex;
  width: 100%;
  justify-content: center;
`;

const App = () => {
  const [toDos, setToDos] = useRecoilState(toDoState);
  const onDragEnd = (info: DropResult) => {
    console.log(info);
    const { destination, source } = info;
    if (!destination) return;
    if (destination?.droppableId === source.droppableId) {
      // same board movement
      setToDos((oldToDos) => {
        const boardCopy = [...oldToDos[source.droppableId]];
        const taskObj = boardCopy[source.index];
        boardCopy.splice(source.index, 1);
        boardCopy.splice(destination?.index, 0, taskObj);
        function setLocalStorage({ boardId, toDos }: IBoardProps) {
          window.localStorage.setItem(boardId, JSON.stringify(toDos));
        }
        // setLocalStorage( source.droppableId , boardCopy );
        console.log(boardCopy);
        return {
          ...oldToDos,
          [source.droppableId]: boardCopy,
        };
      });
    }
    if (destination.droppableId === "delete") {
      // cross board movement
      setToDos((allBoards) => {
        const sourceBoard = [...allBoards[source.droppableId]];
        sourceBoard.splice(source.index, 1);
        return {
          ...allBoards,
          [source.droppableId]: sourceBoard,
        };
      });
    } else if (destination.droppableId !== source.droppableId) {
      // cross board movement
      setToDos((allBoards) => {
        const sourceBoard = [...allBoards[source.droppableId]];
        const taskObj = sourceBoard[source.index];
        const destinationBoard = [...allBoards[destination.droppableId]];
        sourceBoard.splice(source.index, 1);
        destinationBoard.splice(destination?.index, 0, taskObj);
        function getLocalStorage({ boardId }: IBoardProps) {
          return window.localStorage.getItem(boardId);
        }
        return {
          ...allBoards,
          [source.droppableId]: sourceBoard,
          [destination.droppableId]: destinationBoard,
        };
      });
    }
  };
  return (
    <ThemeProvider theme={isLight ? lightTheme : darkTheme}>
      <DragDropContext onDragEnd={onDragEnd}>
        <Wrapper>
          <Boards>
            {Object.keys(toDos).map((boardId) => (
              <Board boardId={boardId} key={boardId} toDos={toDos[boardId]} />
            ))}
          </Boards>
          <DeleteSection>
            <Board boardId="delete" toDos={[]} />
          </DeleteSection>
        </Wrapper>
      </DragDropContext>
    </ThemeProvider>
  );
};

export default App;
