import React, { useEffect } from "react";
import { darkTheme, lightTheme } from "./theme";
import { DragDropContext, DropResult } from "react-beautiful-dnd";
import styled from "styled-components";
import { useRecoilState } from "recoil";
import { isLightState, toDoState } from "./atom";
import Board, { MaterialIcon } from "./components/Board";

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
  gap: 30px;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const Boards = styled.div`
  display: grid;
  width: 100%;
  gap: 10px;
  grid-template-columns: repeat(3, 1fr);
`;

const DeleteSection = styled.div`
  display: flex;
  width: 100%;
  justify-content: center;
`;

const App = () => {
  const [isLight, setIsLight] = useRecoilState(isLightState);
  const toggleTheme = () => setIsLight((curr) => !curr);
  useEffect(() => {
    window // user 설정 테마에 맞춰 페이지 테마 동기화
      .matchMedia("(prefers-color-scheme: light)")
      .addEventListener("change", (e) => {
        console.log(e);
        setIsLight(e.matches);
      });
  });

  const [toDos, setToDos] = useRecoilState(toDoState);

  const onAdd = () => {
    const title = window.prompt("새 보드의 이름을 입력해주세요.")?.trim();
    if (title !== null && title !== undefined) {
      if (title === "") {
        alert("이름을 입력해주세요.");
        return;
      }
      setToDos((prev) => {
        return [...prev, { title, id: Date.now(), toDos: [] }];
      });
    }
  };

  const onDragEnd = ({ draggableId, source, destination }: DropResult) => {
    if (!destination) return;
    if (destination?.droppableId === source.droppableId) {
      // same board movement
      setToDos((oldToDos) => {
        const boardCopy = [...oldToDos[source.droppableId]];
        const taskObj = boardCopy[source.index];
        boardCopy.splice(source.index, 1);
        boardCopy.splice(destination?.index, 0, taskObj);
        return {
          ...oldToDos,
          [source.droppableId]: boardCopy,
        };
      });
    }
    if (destination.droppableId === "delete") {
      // cross board movement
      setToDos((allBoards) => {
        const sourceBoard = [...allBoards[source.droppableId]];
        sourceBoard.splice(source.index, 1);
        return {
          ...allBoards,
          [source.droppableId]: sourceBoard,
        };
      });
    } else if (destination.droppableId !== source.droppableId) {
      // cross board movement
      setToDos((allBoards) => {
        const sourceBoard = [...allBoards[source.droppableId]];
        const taskObj = sourceBoard[source.index];
        const destinationBoard = [...allBoards[destination.droppableId]];
        sourceBoard.splice(source.index, 1);
        destinationBoard.splice(destination?.index, 0, taskObj);
        return {
          ...allBoards,
          [source.droppableId]: sourceBoard,
          [destination.droppableId]: destinationBoard,
        };
      });
    }
  };
  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Wrapper>
        <Boards>
          {Object.keys(toDos).map((boardId) => (
            <Board boardId={boardId} key={boardId} toDos={toDos[boardId]} />
          ))}
        </Boards>
        <DeleteSection>
          <Board boardId="delete" toDos={[]} />
        </DeleteSection>
      </Wrapper>
    </DragDropContext>
  );
};

export default App;
